SELECT t.DEPTNO FROM (SELECT DEPT.DEPTNO FROM DEPT AS DEPT) AS t INNER JOIN (SELECT t0.DEPTNO AS DEPTNO9 FROM (SELECT EMP.SAL, EMP.DEPTNO FROM EMP AS EMP) AS t0 WHERE t0.SAL > 100) AS t2 ON t.DEPTNO = t2.DEPTNO9 INNER JOIN (SELECT ACCOUNT.ACCTNO FROM ACCOUNT AS ACCOUNT) AS t3 ON t.DEPTNO = t3.ACCTNO
