SELECT t4.ENAME FROM (SELECT EMP1.ENAME, EMP1.DEPTNO, EMP1.SAL + 1 AS SALPLUS FROM EMP AS EMP1) AS t4 INNER JOIN (SELECT t5.DEPTNO, t5.expr1 FROM (SELECT EMP2.EMPNO, EMP2.ENAME, EMP2.JOB, EMP2.MGR, EMP2.HIREDATE, EMP2.SAL, EMP2.COMM, EMP2.DEPTNO, EMP2.SLACKER, (EMP2.SAL + 1) AS expr1 FROM EMP AS EMP2) AS t5 WHERE t5.SAL + 1 = t5.expr1 GROUP BY t5.DEPTNO, t5.expr1) AS t8 ON t4.SALPLUS = t8.expr1 AND t4.DEPTNO = t8.DEPTNO
