SELECT S.DEPTNO FROM (SELECT DEPT.DEPTNO, DEPT.NAME FROM DEPT AS DEPT WHERE EXISTS (SELECT EMP.EMPNO, EMP.ENAME, EMP.JOB, EMP.MGR, EMP.HIREDATE, EMP.SAL, EMP.COMM, EMP.DEPTNO, EMP.SLACKER FROM EMP AS EMP WHERE EMP.DEPTNO = DEPT.DEPTNO AND EMP.SAL > 100)) AS S INNER JOIN ACCOUNT AS ACCOUNT ON S.DEPTNO = ACCOUNT.ACCTNO