SELECT topics.* FROM topics LEFT JOIN topic_users AS tu ON topics.id = tu.topic_id AND tu.user_id = 13455 WHERE topics.deleted_at IS NULL AND topics.archetype = 'private_message' AND topics.id IN (SELECT topic_id FROM topic_allowed_users WHERE user_id = 13455) AND (topics.id IN (SELECT ta.topic_id FROM topic_allowed_users AS ta WHERE ta.user_id IN (2, 1995, 8307, 17621, 22980, -10)) OR topics.id IN (SELECT tg.topic_id FROM topic_allowed_groups AS tg WHERE tg.group_id IN (-10))) AND NOT topics.id IN (68559, 60069, 42145) AND topics.visible = 't' ORDER BY topics.bumped_at DESC