SELECT * FROM categories WHERE categories.id IN (SELECT c.id FROM categories AS c WHERE NOT c.read_restricted IS TRUE AND NOT EXISTS (SELECT 1 FROM category_groups AS cg WHERE cg.category_id = categories.id) OR EXISTS (SELECT 1 FROM category_groups AS cg WHERE permission_type IN (2081219153) AND cg.category_id = categories.id AND group_id IN (SELECT g.group_id FROM group_users g where g.user_id = 1420)))