SELECT merge_requests.* FROM merge_requests INNER JOIN label_links ON label_links.target_id = merge_requests.id AND label_links.target_type = 'MergeRequest' INNER JOIN labels ON labels.id = label_links.label_id WHERE merge_requests.state_id IN (1) AND labels.title = 'bug' AND labels.id IN (SELECT labels.id FROM labels WHERE labels.id IN (SELECT labels.id FROM labels WHERE labels.project_id IN (SELECT projects.id FROM projects WHERE projects.id = 13083)) ORDER BY labels.title ASC) AND merge_requests.target_project_id = 13083 ORDER BY merge_requests.id DESC