SELECT DISTINCT spree_products.* FROM spree_products INNER JOIN spree_products_taxons ON spree_products_taxons.product_id = spree_products.id INNER JOIN spree_taxons ON spree_taxons.id = spree_products_taxons.taxon_id INNER JOIN spree_variants ON spree_variants.deleted_at IS NULL AND spree_variants.is_master = 0 AND spree_variants.product_id = spree_products.id INNER JOIN spree_prices ON spree_prices.deleted_at IS NULL AND spree_prices.variant_id = spree_variants.id WHERE spree_products.deleted_at IS NULL AND spree_taxons.id IN (1, 2) AND (spree_products.deleted_at IS NULL OR spree_products.deleted_at >= '2020-05-24 13:55:57.476059') AND (spree_products.discontinue_on IS NULL OR spree_products.discontinue_on >= '2020-05-24 13:55:57.476251') AND spree_products.available_on <= '2020-05-24 13:55:57.476241'