select count(adminpermi0_.ADMIN_PERMISSION_ID) as col_0_0_ from BLC_ADMIN_PERMISSION adminpermi0_ inner join BLC_ADMIN_USER_PERMISSION_XREF allusers1_ on adminpermi0_.ADMIN_PERMISSION_ID=allusers1_.ADMIN_PERMISSION_ID inner join BLC_ADMIN_USER adminuseri2_ on allusers1_.ADMIN_USER_ID=adminuseri2_.ADMIN_USER_ID left outer join BLC_ADMIN_USER_SANDBOX adminuseri2_1_ on adminuseri2_.ADMIN_USER_ID=adminuseri2_1_.ADMIN_USER_ID where adminpermi0_.IS_FRIENDLY=1 and adminuseri2_.ADMIN_USER_ID=1