SELECT users.*, user_options.timezone, group_users.created_at as added_at FROM "users" INNER JOIN "group_users" ON "users"."id" = "group_users"."user_id" INNER JOIN "user_options" ON "user_options"."user_id" = "users"."id" WHERE "group_users"."group_id" = 2564 AND (users.id > 0) ORDER BY NOT group_users.owner, last_posted_at DESC NULLS LAST, "users"."username_lower" DESC LIMIT 50 OFFSET 0