SELECT users.*, user_options.timezone, group_users.created_at as added_at FROM "users" INNER JOIN "group_users" ON "users"."id" = "group_users"."user_id" INNER JOIN "user_options" ON "user_options"."user_id" = "users"."id" WHERE "group_users"."group_id" = 2566 AND (users.id > 0) AND (username_lower ILIKE '%uniquetest@discourse.org%') AND (group_users.owner) ORDER BY "users"."username_lower" ASC