SELECT "topics".* FROM "topics" WHERE ("topics"."deleted_at" IS NOT NULL) AND (topics.category_id IS NULL OR topics.category_id IN (SELECT id FROM categories WHERE NOT read_restricted)) AND (topics.archetype <> 'private_message') AND "topics"."visible" = TRUE AND "topics"."user_id" = 828 ORDER BY like_count DESC, created_at DESC LIMIT 6