SELECT category_id, COUNT(*) FROM "topics" WHERE ("topics"."deleted_at" IS NOT NULL) AND (topics.archetype <> 'private_message') AND "topics"."visible" = TRUE AND (topics.category_id IS NULL OR topics.category_id IN (SELECT id FROM categories WHERE NOT read_restricted)) AND (topics.category_id in (NULL)) AND "topics"."user_id" = 828 GROUP BY topics.category_id ORDER BY COUNT(*) DESC