SELECT "posts".* FROM "posts" INNER JOIN (SELECT "topics"."id" FROM "topics" LEFT OUTER JOIN topic_users ON topic_users.topic_id = topics.id AND topic_users.user_id = 831 LEFT OUTER JOIN category_users ON category_users.category_id = topics.category_id AND category_users.user_id = 831 LEFT OUTER JOIN users ON users.id = topics.user_id WHERE ("topics"."deleted_at" IS NOT NULL) AND "topics"."visible" = TRUE AND (topics.category_id IS NULL OR topics.category_id IN (SELECT id FROM categories WHERE NOT read_restricted)) AND "topics"."closed" = FALSE AND "topics"."archived" = FALSE AND (COALESCE(topic_users.notification_level, 1) <> 0) AND (topics.created_at > '1970-01-01 00:00:00') AND (topics.created_at < '2020-03-26 06:10:34.986288') AND (topics.archetype <> 'private_message') AND (COALESCE(users.trust_level, 0) > 0)) AS digest_topics ON digest_topics.id = posts.topic_id WHERE ("posts"."deleted_at" IS NOT NULL) AND (posts.created_at > '2020-02-26 06:15:34.974440') AND "posts"."post_type" != 4 AND (posts.post_type = 1) AND (posts.deleted_at IS NOT NULL AND posts.hidden = false AND posts.user_deleted = false) AND (posts.post_number > 1 AND posts.score > 75.0) AND (posts.created_at < '2020-03-26 06:10:34.988345') ORDER BY posts.score DESC, posts.created_at ASC LIMIT 5