SELECT "posts"."id" FROM (SELECT "posts".* FROM "posts" INNER JOIN "users" ON "users"."id" = "posts"."user_id" WHERE ("posts"."deleted_at" IS NOT NULL) AND "posts"."topic_id" = 15986 AND (posts.user_id = 915 OR post_type IN (1,2,3)) AND (posts.post_type = 1) AND (post_number > 1) ORDER BY percent_rank asc, sort_order asc LIMIT 5) posts WHERE ("posts"."deleted_at" IS NOT NULL) ORDER BY "posts"."post_number" ASC