SELECT "posts"."id" FROM "posts" LEFT OUTER JOIN "users" ON "users"."id" = "posts"."user_id" LEFT OUTER JOIN "groups" ON "groups"."id" = "users"."primary_group_id" LEFT OUTER JOIN "users" "reply_to_users_posts" ON "reply_to_users_posts"."id" = "posts"."reply_to_user_id" LEFT OUTER JOIN "users" "deleted_bies_posts" ON "deleted_bies_posts"."id" = "posts"."deleted_by_id" LEFT OUTER JOIN "incoming_emails" ON "incoming_emails"."post_id" = "posts"."id" LEFT OUTER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) WHERE ("posts"."deleted_at" IS NOT NULL) AND "posts"."id" = 16898 AND "posts"."topic_id" = 15986 AND (posts.user_id = 915 OR post_type IN (1,2,3)) ORDER BY "posts"."sort_order" DESC