SELECT DISTINCT "tags"."name" FROM "tags" INNER JOIN "tag_group_memberships" ON "tag_group_memberships"."tag_id" = "tags"."id" INNER JOIN "tag_groups" ON "tag_groups"."id" = "tag_group_memberships"."tag_group_id" INNER JOIN "tag_group_permissions" ON "tag_group_permissions"."tag_group_id" = "tag_groups"."id" WHERE (tag_group_permissions.group_id = 0 AND tag_group_permissions.permission_type = 3)