SELECT "groups"."id" FROM "groups" WHERE (groups.id IN ( SELECT group_id FROM category_groups JOIN groups g ON group_id = g.id WHERE category_id = 3182 AND user_count < 500 ) ) AND (groups.id > 0) AND ( groups.id IN ( SELECT id FROM groups WHERE members_visibility_level = 0 UNION ALL SELECT id FROM groups WHERE members_visibility_level = 1 AND 1492 IS NOT NULL UNION ALL SELECT g.id FROM groups g JOIN group_users gu ON gu.group_id = g.id AND gu.user_id = 1492 WHERE g.members_visibility_level = 2 UNION ALL SELECT g.id FROM groups g LEFT JOIN group_users gu ON gu.group_id = g.id AND gu.user_id = 1492 AND gu.owner WHERE g.members_visibility_level = 3 AND (gu.id IS NOT NULL OR FALSE) UNION ALL SELECT g.id FROM groups g JOIN group_users gu ON gu.group_id = g.id AND gu.user_id = 1492 AND gu.owner WHERE g.members_visibility_level = 4 ) ) ORDER BY name ASC