SELECT "posts"."id", "posts"."cooked" FROM "posts" INNER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) LEFT JOIN post_custom_fields ON posts.id = post_custom_fields.post_id AND post_custom_fields.name = 'missing uploads ignored' WHERE ("posts"."deleted_at" IS NOT NULL) AND ( ( posts.cooked LIKE '%<a %' OR posts.cooked LIKE '%<img %' OR posts.cooked LIKE '%<video %' ) AND ( posts.cooked LIKE '%/uploads/default/%' OR posts.cooked LIKE '%/original/%' OR posts.cooked LIKE '%/optimized/%' OR posts.cooked LIKE '%data-orig-src=%' OR posts.cooked LIKE '%/uploads/short-url/%' )) AND (post_custom_fields.id IS NULL) ORDER BY "posts"."id" ASC LIMIT 1000