SELECT "incoming_emails"."to_addresses", "incoming_emails"."cc_addresses" FROM "incoming_emails" INNER JOIN "posts" ON "posts"."id" = "incoming_emails"."post_id" AND ("posts"."deleted_at" IS NOT NULL) WHERE (posts.topic_id = 16464) AND (incoming_emails.to_addresses ILIKE '%4f97315cc828096c9cb34c6f1a0d6fe8%' OR incoming_emails.cc_addresses ILIKE '%4f97315cc828096c9cb34c6f1a0d6fe8%' ) AND (EXISTS( SELECT 1 FROM user_emails WHERE user_emails.user_id = 1943 AND (incoming_emails.to_addresses ILIKE '%' || user_emails.email || '%' OR incoming_emails.cc_addresses ILIKE '%' || user_emails.email || '%') ) )