SELECT "incoming_emails"."post_id", "incoming_emails"."to_addresses", "incoming_emails"."cc_addresses" FROM "incoming_emails" WHERE "incoming_emails"."message_id" = '34@foo.bar.mail' AND (EXISTS( SELECT 1 FROM user_emails WHERE user_emails.user_id = 1967 AND (incoming_emails.to_addresses ILIKE '%' || user_emails.email || '%' OR incoming_emails.cc_addresses ILIKE '%' || user_emails.email || '%') ) )