SELECT "posts"."id" FROM (SELECT "posts".* FROM "posts" INNER JOIN "users" ON "users"."id" = "posts"."user_id" WHERE "posts"."topic_id" = 16517 AND (posts.user_id = 2113 OR post_type IN (1,2,3,4)) AND "posts"."hidden" = FALSE AND (posts.post_type = 1) AND (post_number > 1) AND (COALESCE(users.trust_level,0) >= 1) ORDER BY percent_rank asc, sort_order asc LIMIT 3) posts WHERE ("posts"."deleted_at" IS NOT NULL) ORDER BY "posts"."post_number" ASC