SELECT "topics"."id" FROM "topics" LEFT OUTER JOIN "categories" ON "categories"."id" = "topics"."category_id" WHERE (topics.archetype <> 'private_message') AND (COALESCE(categories.topic_id, 0) <> topics.id) AND "topics"."visible" = TRUE AND (topics.deleted_at IS NOT NULL) AND (topics.category_id IS NULL or topics.category_id IN (1)) AND "topics"."closed" = FALSE AND "topics"."archived" = FALSE AND (topics.created_at > '2019-03-27 06:17:58.510585') ORDER BY RANDOM() LIMIT 3000