SELECT "topics".* FROM "topics" LEFT JOIN topic_users tu ON topics.id = tu.topic_id AND tu.user_id = 2114 LEFT JOIN topic_allowed_users ta ON topics.id = ta.topic_id AND ta.user_id = 2114 LEFT JOIN topic_allowed_users ta2 ON topics.id = ta2.topic_id AND ta2.user_id IN (2181,2258) WHERE ("topics"."deleted_at" IS NOT NULL) AND (topics.archetype = 'private_message') AND (ta.topic_id IS NOT NULL) AND (ta2.topic_id IS NOT NULL) AND (topics.id NOT IN (16796)) AND "topics"."visible" = TRUE ORDER BY topics.bumped_at DESC LIMIT 6