SELECT "posts"."id" FROM "posts" WHERE ("posts"."deleted_at" IS NOT NULL) AND "posts"."topic_id" = 17061 AND "posts"."post_type" IN (1, 2, 3) AND "posts"."topic_id" = 17061 AND (id = ANY( ( SELECT posts.id FROM posts WHERE posts.topic_id = 17061 AND posts.post_number = 1 ) UNION ( SELECT p1.id FROM posts p1 WHERE p1.percent_rank <= 0.2 AND p1.topic_id = 17061 ORDER BY p1.percent_rank LIMIT 100 ) )) AND (sort_order > 1) ORDER BY "posts"."sort_order" ASC LIMIT 2