SELECT DISTINCT "web_hooks".* FROM "web_hooks" INNER JOIN "web_hook_event_types_hooks" ON "web_hook_event_types_hooks"."web_hook_id" = "web_hooks"."id" INNER JOIN "web_hook_event_types" ON "web_hook_event_types"."id" = "web_hook_event_types_hooks"."web_hook_event_type_id" WHERE "web_hooks"."active" = TRUE AND (web_hooks.wildcard_web_hook = TRUE OR web_hook_event_types.name = 'post') ORDER BY id ASC