SELECT "groups"."name" FROM "groups" INNER JOIN "category_groups" ON "groups"."id" = "category_groups"."group_id" WHERE "category_groups"."category_id" = 3276 AND (groups.id > 0) AND ( groups.id IN ( SELECT id FROM groups WHERE visibility_level = 0 UNION ALL SELECT id FROM groups WHERE visibility_level = 1 AND 2112 IS NOT NULL UNION ALL SELECT g.id FROM groups g JOIN group_users gu ON gu.group_id = g.id AND gu.user_id = 2112 WHERE g.visibility_level = 2 UNION ALL SELECT g.id FROM groups g LEFT JOIN group_users gu ON gu.group_id = g.id AND gu.user_id = 2112 AND gu.owner WHERE g.visibility_level = 3 AND (gu.id IS NOT NULL OR FALSE) UNION ALL SELECT g.id FROM groups g JOIN group_users gu ON gu.group_id = g.id AND gu.user_id = 2112 AND gu.owner WHERE g.visibility_level = 4 ) ) AND "groups"."automatic" = FALSE ORDER BY name ASC