SELECT "posts".* FROM "posts" WHERE ("posts"."deleted_at" IS NOT NULL) AND (baked_at >= '2020-02-25 06:18:48.933850') AND ( ( posts.cooked LIKE '%<a %' OR posts.cooked LIKE '%<img %' OR posts.cooked LIKE '%<video %' ) AND ( posts.cooked LIKE '%/uploads/default/%' OR posts.cooked LIKE '%/original/%' OR posts.cooked LIKE '%/optimized/%' OR posts.cooked LIKE '%data-orig-src=%' OR posts.cooked LIKE '%/uploads/short-url/%' )) ORDER BY "posts"."id" ASC LIMIT 1000