SELECT "posts"."id", "posts"."created_at", "posts"."user_id" FROM "posts" INNER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) WHERE ("posts"."deleted_at" IS NOT NULL) AND (posts.post_type IN (1,2,3)) AND (topics.visible = true) AND "posts"."hidden" = FALSE AND (topics.archetype <> 'private_message') AND "posts"."via_email" = TRUE AND (post_number > 1) ORDER BY "posts"."id" ASC LIMIT 1000