SELECT "posts".* FROM "posts" WHERE ("posts"."deleted_at" IS NOT NULL) AND "posts"."deleted_at" IS NOT NULL AND "posts"."user_deleted" = TRUE AND (NOT EXISTS ( SELECT 1 FROM topics t WHERE t.deleted_at IS NOT NULL AND t.id = posts.topic_id )) AND (updated_at < '2020-03-25 06:20:15.004316') AND (NOT EXISTS ( SELECT 1 FROM post_actions pa WHERE pa.post_id = posts.id AND pa.deleted_at IS NOT NULL AND pa.deferred_at IS NOT NULL AND pa.post_action_type_id IN (3,4,8,7) )) ORDER BY "posts"."id" ASC LIMIT 1000