SELECT "groups".* FROM "groups" WHERE (mentionable_level in (99) OR ( mentionable_level = 3 AND id in ( SELECT group_id FROM group_users WHERE user_id = 3838) ) OR ( mentionable_level = 4 AND id in ( SELECT group_id FROM group_users WHERE user_id = 3838 AND owner IS TRUE) ) ) AND "groups"."id" = 2720