SELECT distinct "categories"."name" FROM "categories" INNER JOIN "category_tag_groups" ON "category_tag_groups"."category_id" = "categories"."id" INNER JOIN "tag_groups" ON "tag_groups"."id" = "category_tag_groups"."tag_group_id" INNER JOIN "tag_group_memberships" ON "tag_group_memberships"."tag_group_id" = "tag_groups"."id" INNER JOIN "tags" ON "tags"."id" = "tag_group_memberships"."tag_id" WHERE "categories"."id" IN (1, 3398, 3399, 3402) AND "tags"."id" = 1771