SELECT COUNT(*) FROM "topics" INNER JOIN "posts" ON "posts"."topic_id" = "topics"."id" AND ("posts"."deleted_at" IS NOT NULL) WHERE ("topics"."deleted_at" IS NOT NULL) AND (topics.archetype <> 'private_message') AND (topics.category_id IS NULL OR topics.category_id IN (SELECT id FROM categories WHERE NOT read_restricted)) AND ("posts"."deleted_at" IS NOT NULL) AND (posts.created_at > '2020-02-25 06:14:16.780956')