SELECT COUNT(*) FROM "reviewable_scores" INNER JOIN "reviewables" ON "reviewables"."id" = "reviewable_scores"."reviewable_id" INNER JOIN "topics" ON "topics"."id" = "reviewables"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) WHERE (reviewables.type = 'ReviewableFlaggedPost') AND (reviewable_scores.created_at >= '2020-01-26 00:00:00' and reviewable_scores.created_at < '2020-02-25 00:00:00')