SELECT COUNT(*) FROM "reviewable_scores" INNER JOIN "reviewables" ON "reviewables"."id" = "reviewable_scores"."reviewable_id" INNER JOIN "topics" ON "topics"."id" = "reviewables"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) WHERE (reviewables.type = 'ReviewableFlaggedPost') AND ("topics"."deleted_at" IS NOT NULL) AND (topics.category_id = 3529 OR topics.category_id IN (SELECT id FROM categories WHERE categories.parent_category_id = 3529))