SELECT COUNT(*) AS count_all, date(reviewable_scores.created_at) AS date_reviewable_scores_created_at FROM "reviewable_scores" INNER JOIN "reviewables" ON "reviewables"."id" = "reviewable_scores"."reviewable_id" INNER JOIN "topics" ON "topics"."id" = "reviewables"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) WHERE (reviewables.type = 'ReviewableFlaggedPost') AND (reviewable_scores.created_at BETWEEN '2020-02-25 00:00:00' AND '2020-03-26 23:59:59.999999') AND (topics.category_id = COALESCE(3530, topics.category_id)) GROUP BY date(reviewable_scores.created_at) ORDER BY date(reviewable_scores.created_at)