SELECT COUNT(*) AS count_all, date(post_actions.created_at) AS date_post_actions_created_at FROM "post_actions" INNER JOIN "posts" ON "posts"."id" = "post_actions"."post_id" AND ("posts"."deleted_at" IS NOT NULL) INNER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) WHERE "post_actions"."post_action_type_id" = 2 AND (post_actions.created_at >= '2020-02-25 00:00:00') AND (post_actions.created_at <= '2020-03-26 23:59:59.999999') AND ("topics"."deleted_at" IS NOT NULL) AND (topics.category_id = 3545 OR topics.category_id IN (SELECT id FROM categories WHERE categories.parent_category_id = 3545)) GROUP BY date(post_actions.created_at) ORDER BY date(post_actions.created_at)