SELECT COUNT(*) FROM "post_actions" INNER JOIN "posts" ON "posts"."id" = "post_actions"."post_id" AND ("posts"."deleted_at" IS NOT NULL) INNER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) WHERE "post_actions"."post_action_type_id" = 2 AND ("topics"."deleted_at" IS NOT NULL) AND (topics.category_id = 3545 OR topics.category_id IN (SELECT id FROM categories WHERE categories.parent_category_id = 3545)) AND (post_actions.created_at >= '2020-01-26 00:00:00' and post_actions.created_at < '2020-02-25 00:00:00')