SELECT COUNT(*) AS count_all, date(us.first_post_created_at) AS date_us_first_post_created_at FROM "users" INNER JOIN user_stats AS us ON us.user_id = users.id WHERE (users.id > 0) AND (NOT EXISTS( SELECT 1 FROM anonymous_users a WHERE a.user_id = users.id )) AND (us.first_post_created_at > '2020-02-25 00:00:00' AND us.first_post_created_at < '2020-03-26 23:59:59.999999') GROUP BY date(us.first_post_created_at) ORDER BY date(us.first_post_created_at)