SELECT COUNT(*) FROM "posts" INNER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) WHERE ("posts"."deleted_at" IS NOT NULL) AND (topics.archetype <> 'private_message') AND "posts"."post_type" = 1 AND ("topics"."deleted_at" IS NOT NULL) AND (topics.category_id = 3559 OR topics.category_id IN (SELECT id FROM categories WHERE categories.parent_category_id = 3559))