SELECT COUNT(*) AS count_all, date(posts.created_at) AS date_posts_created_at FROM "posts" INNER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) WHERE ("posts"."deleted_at" IS NOT NULL) AND (posts.user_id > 0) AND (topics.archetype = 'private_message') AND (topics.subtype = 'user_to_user') AND (posts.created_at >= '2020-02-25 00:00:00' AND posts.created_at <= '2020-03-26 23:59:59.999999') GROUP BY date(posts.created_at) ORDER BY date(posts.created_at)