SELECT COUNT(*) FROM "posts" INNER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) WHERE ("posts"."deleted_at" IS NOT NULL) AND (topics.archetype = 'private_message') AND (posts.user_id > 0) AND (topics.subtype = 'user_to_user') AND (posts.created_at >= '2020-01-26 00:00:00' and posts.created_at < '2020-02-25 00:00:00')