SELECT "topics"."id" FROM "topics" LEFT OUTER JOIN "categories" ON "categories"."id" = "topics"."category_id" WHERE (categories.id in (3574) AND ( categories.topic_id <> topics.id OR categories.id = 3574 ) ) AND (topics.archetype <> 'private_message') AND (COALESCE(categories.topic_id, 0) <> topics.id) AND "topics"."visible" = TRUE AND NOT (("topics"."id" IN (18172, 18170, 18171) OR "topics"."id" IS NULL)) AND (topics.deleted_at IS NOT NULL) AND (topics.category_id IS NULL or topics.category_id IN (1,3572,3574)) AND (topics.pinned_at IS NOT NULL AND topics.category_id = 3574) ORDER BY topics.bumped_at DESC, pinned_at DESC