SELECT COUNT(DISTINCT "directory_items"."id") FROM "directory_items" LEFT OUTER JOIN "users" ON "users"."id" = "directory_items"."user_id" LEFT OUTER JOIN "group_users" ON "group_users"."user_id" = "users"."id" LEFT OUTER JOIN "groups" ON "groups"."id" = "group_users"."group_id" LEFT OUTER JOIN "user_stats" ON "user_stats"."user_id" = "directory_items"."user_id" WHERE "directory_items"."period_type" = 1 AND "groups"."id" = 2898