SELECT COUNT(DISTINCT "directory_items"."id") FROM "directory_items" LEFT OUTER JOIN "users" ON "users"."id" = "directory_items"."user_id" LEFT OUTER JOIN "groups" ON "groups"."id" = "users"."primary_group_id" LEFT OUTER JOIN "user_stats" ON "user_stats"."user_id" = "directory_items"."user_id" WHERE "directory_items"."period_type" = 1 AND "users"."username" NOT IN ('stage_user', 'eviltrout')