SELECT distinct "posts".* FROM "posts" INNER JOIN "post_search_data" ON "post_search_data"."post_id" = "posts"."id" INNER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) LEFT JOIN categories ON categories.id = topics.category_id WHERE ("posts"."deleted_at" IS NOT NULL) AND "posts"."post_type" IN (1, 2, 3) AND (topics.visible) AND (topics.archetype <> 'private_message') AND (topics.category_id IN (3715)) AND (topics.id IN ( SELECT DISTINCT(tt.topic_id) FROM topic_tags tt, tags WHERE tt.tag_id = tags.id AND lower(tags.name) IN ('lunch') )) AND ((categories.id IS NULL) OR (NOT categories.read_restricted)) AND (posts.topic_id NOT in (18677,18676)) ORDER BY posts.created_at DESC LIMIT 12 OFFSET 0