SELECT distinct "posts".* FROM "posts" INNER JOIN "post_search_data" ON "post_search_data"."post_id" = "posts"."id" INNER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) LEFT JOIN categories ON categories.id = topics.category_id LEFT JOIN topic_tags ON topic_tags.topic_id = posts.topic_id WHERE ("posts"."deleted_at" IS NOT NULL) AND "posts"."post_type" IN (1, 2, 3) AND (topics.visible) AND (topics.archetype <> 'private_message') AND (topic_tags.id IS NULL) AND NOT (categories.search_priority = 1) AND ((categories.id IS NULL) OR (NOT categories.read_restricted)) ORDER BY posts.created_at DESC LIMIT 18 OFFSET 0