SELECT "users".* FROM "users" INNER JOIN "topic_users" ON "topic_users"."user_id" = "users"."id" WHERE (users.id > 0) AND (NOT EXISTS( SELECT 1 FROM anonymous_users a WHERE a.user_id = users.id )) AND "users"."staged" = FALSE AND "users"."id" != 7022 AND "topic_users"."last_read_post_number" IS NOT NULL AND (topic_users.topic_id = 18850 AND topic_users.last_read_post_number >= 3) AND (admin OR moderator)