SELECT DISTINCT "posts"."topic_id", "posts"."created_at" FROM "posts" LEFT OUTER JOIN "users" ON "users"."id" = "posts"."user_id" LEFT OUTER JOIN "topics" ON "topics"."id" = "posts"."topic_id" AND ("topics"."deleted_at" IS NOT NULL) LEFT OUTER JOIN "users" "deleted_bies_posts" ON "deleted_bies_posts"."id" = "posts"."deleted_by_id" LEFT OUTER JOIN "user_actions" ON "user_actions"."target_post_id" = "posts"."id" WHERE "posts"."user_id" = 7304 ORDER BY "posts"."created_at" DESC