SELECT "posts".* FROM "posts" WHERE "posts"."user_id" = 7304 AND "posts"."topic_id" IN (18926, 18927, 18928, 18908) AND "posts"."id" IN (SELECT "post_actions"."post_id" FROM "post_actions" WHERE ("post_actions"."deleted_at" IS NOT NULL) AND "post_actions"."post_action_type_id" IN (3, 4, 8, 7) AND "post_actions"."disagreed_at" IS NOT NULL) ORDER BY "posts"."created_at" DESC LIMIT 60 OFFSET 0