SELECT "topics".* FROM "topics" LEFT OUTER JOIN topic_users AS tu ON (topics.id = tu.topic_id AND tu.user_id = 7462) WHERE (topics.archetype <> 'private_message') AND (topics.deleted_at IS NOT NULL) AND (tu.last_read_post_number < topics.highest_staff_post_number) AND (COALESCE(tu.notification_level, 1) >= 2) AND (topics.updated_at >= '2020-03-26 06:25:40.042270') ORDER BY CASE WHEN topics.user_id = tu.user_id THEN 1 ELSE 2 END, topics.bumped_at DESC LIMIT 30